import React from 'react';

const Checkout = () => {

  const loadRazorpay = async () => {
    const res = await loadScript("https://checkout.razorpay.com/v1/checkout.js");
    if (!res) {
      alert("Razorpay SDK failed to load. Are you online?");
      return;
    }

    // 1️⃣ Call backend to create Razorpay order
    const orderData = await fetch("http://localhost:8000/payment/orders", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ amount: 500 }), // 💰 Amount in INR (500 = ₹500)
    }).then(res => res.json());

    const options = {
      key: "CJukFFYASRCTtNK4knfpytIt", // 🔑 Replace with yours (or use process.env)
      amount: orderData.amount,
      currency: "INR",
      name: "Your Company",
      description: "Test Transaction",
      order_id: orderData.id,
      handler: async function (response) {
        // 2️⃣ Verify payment on backend
        const verifyRes = await fetch("http://localhost:8000/payment/verify", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify(response),
        });

        const result = await verifyRes.json();
        alert(result.message);
      },
      theme: {
        color: "#3399cc",
      },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  // Helper to load Razorpay script
  function loadScript(src) {
    return new Promise((resolve) => {
      const script = document.createElement("script");
      script.src = src;
      script.onload = () => resolve(true);
      script.onerror = () => resolve(false);
      document.body.appendChild(script);
    });
  }

  return (
    <div style={{ textAlign: "center", marginTop: "100px" }}>
      <h2>Pay ₹500</h2>
      <button onClick={loadRazorpay} style={{ padding: "10px 20px", fontSize: "18px" }}>
        Pay Now
      </button>
    </div>
  );
};

export default Checkout;
