import React from 'react';

const Home = () => {
  const handlePayment = async () => {
    // Step 1: Call backend to create Razorpay order
    const res = await fetch('http://localhost:8000/payment/create-order', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ amount: 5 }) 
    });

    const data = await res.json();
    if (!data.success) return alert("Error in creating order");

    const options = {
      key: "rzp_test_yourKeyHere", // Replace with your Razorpay Key ID
      amount: data.order.amount,
      currency: "INR",
      name: "Your Company Name",
      description: "Test Transaction",
      order_id: data.order.id,
      handler: function (response) {
        alert("Payment successful! ðŸŽ‰");
        console.log(response);
        // Optional: Send response.razorpay_payment_id to backend to verify
      },
      prefill: {
        name: "Shivang Vats",
        email: "shivang@example.com",
      },
      theme: {
        color: "#4c00b4",
      },
    };

    const razor = new window.Razorpay(options);
    razor.open();
  };

  return (
    <div style={{ padding: '40px' }}>
      <h1>Welcome to Home Page</h1>
      <button onClick={handlePayment} style={{ padding: '10px 20px', fontSize: '18px' }}>
        Pay â‚¹50 Now
      </button>
    </div>
  );
};

export default Home;
