import React from 'react';

const Home = () => {
  const handlePayment = async () => {
    try {
      // Step 1: Create Razorpay order from backend
      const res = await fetch('http://localhost:8000/payment/create-order', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ amount: 5 }) // ₹5
      });

      const data = await res.json();

      if (!data.success) {
        alert("Error in creating order");
        return;
      }

      // Step 2: Razorpay options
      const options = {
        key: "uNqEEoBOkhmuDBT3iV5vak34",
        amount: data.order.amount,
        currency: data.order.currency,
        name: "Shivang Store",
        description: "Test ₹5 Payment",
        order_id: data.order.id,
        handler: function (response) {
          alert(" Payment successful!");
          console.log("Payment response:", response);
        },
        prefill: {
          name: "Shivang Vats",
          email: "shivang@example.com"
        },
        theme: {
          color: "#4c00b4"
        }
      };

      const razor = new window.Razorpay(options);
      razor.open();
    } catch (err) {
      console.error("Payment error:", err);
      alert("Something went wrong during payment");
    }
  };

  return (
    <div style={{ textAlign: 'center', padding: '60px' }}>
      <h1>Welcome to Home Page</h1>
      <p>You are successfully logged in.</p>

      <button
        onClick={handlePayment}
        style={{
          padding: '12px 24px',
          fontSize: '16px',
          backgroundColor: '#4c00b4',
          color: '#fff',
          border: 'none',
          borderRadius: '6px',
          cursor: 'pointer',
          marginTop: '20px'
        }}
      >
        Pay ₹5 Now
      </button>
    </div>
  );
};

export default Home;
